      IF(EFINAL.LE.EIN(4)) NIN=3                                        
      IF(EFINAL.LE.EIN(3)) NIN=2                                        
      IF(EFINAL.LE.EIN(2)) NIN=1                                        
      IF(EFINAL.LE.EIN(1)) NIN=0                                        
