C CALCULATE DIPOLE ANGULAR DISTRIBUTION  FACTOR FOR TRANSITION
      EPOINT=EN/DABS(EIN(90))
      IF(EPOINT.GT.500.) GO TO 668 
      DO 666  M=1,NRTANG
      IF(EPOINT.LE.ENROT(M)) GO TO 667 
  666 CONTINUE
      M=NRTANG
  667 A=(YEPSR(M)-YEPSR(M-1))/(ENROT(M)-ENROT(M-1))
      B=(ENROT(M-1)*YEPSR(M)-ENROT(M)*YEPSR(M-1))/(ENROT(M-1)-ENROT(M))
      EPSIL=A*EPOINT+B
      PEQIN(90,I)=1.0-EPSIL
      GO TO 670 
  668 PEQIN(90,I)=PEQEL(2,(I-IOFFN(90)))
C DIPOLE ALLOWED                  ELOSS=28.50        F=0.0067791
  670 IF(EN.LE.EIN(91)) GO TO 675 
      QIN(91,I)=.0067791/(EIN(91)*BETA2)*(DLOG(BETA2*GAMMA2*EMASS2/(4.0*
     /EIN(91)))-BETA2-DEN(I)/2.0)*BBCONST*EN/(EN+EIN(91)+E(3))         
      IF(QIN(91,I).LT.0.0) QIN(91,I)=0.0
C CALCULATE DIPOLE ANGULAR DISTRIBUTION  FACTOR FOR TRANSITION
      EPOINT=EN/DABS(EIN(91))
      IF(EPOINT.GT.500.) GO TO 673 
