      EPSIL=A*EPOINT+B
      PEQIN(91,I)=1.0-EPSIL
      GO TO 675 
  673 PEQIN(91,I)=PEQEL(2,(I-IOFFN(91)))
C DIPOLE ALLOWED                  ELOSS=29.50        F=0.00091485
  675 IF(EN.LE.EIN(92)) GO TO 680 
      QIN(92,I)=.0009148/(EIN(92)*BETA2)*(DLOG(BETA2*GAMMA2*EMASS2/(4.0*
     /EIN(92)))-BETA2-DEN(I)/2.0)*BBCONST*EN/(EN+EIN(92)+E(3))         
      IF(QIN(92,I).LT.0.0) QIN(92,I)=0.0
C CALCULATE DIPOLE ANGULAR DISTRIBUTION  FACTOR FOR TRANSITION
      EPOINT=EN/DABS(EIN(92))
      IF(EPOINT.GT.500.) GO TO 678 
      DO 676  M=1,NRTANG
      IF(EPOINT.LE.ENROT(M)) GO TO 677 
  676 CONTINUE
      M=NRTANG
  677 A=(YEPSR(M)-YEPSR(M-1))/(ENROT(M)-ENROT(M-1))
      B=(ENROT(M-1)*YEPSR(M)-ENROT(M)*YEPSR(M-1))/(ENROT(M-1)-ENROT(M))
      EPSIL=A*EPOINT+B
      PEQIN(92,I)=1.0-EPSIL
      GO TO 680 
  678 PEQIN(92,I)=PEQEL(2,(I-IOFFN(92)))
C 
C  TRIPLET                          ELOSS=32.00 EV
  680 IF(EN.LE.EIN(93)) GO TO 685     
      IF(EN.GT.XTRP6(NTRP6)) GO TO 683 
      DO 681 J=2,NTRP6
      IF(EN.LE.(XTRP6(J))) GO TO 682 
  681 CONTINUE
      J=NTRP6
  682 Y1=DLOG(YTRP6(J))
      Y2=DLOG(YTRP6(J-1))
      X1=DLOG(XTRP6(J))
      X2=DLOG(XTRP6(J-1))
      A=(Y1-Y2)/(X1-X2)
      B=(X2*Y1-X1*Y2)/(X2-X1)
      QIN(93,I)=DEXP(A*DLOG(EN)+B)*1.D-16*AMPTRP6                    
      GO TO 684 
C SCALE BY 1/E**2 ABOVE XTRP6(NTRP6) EV
  683 QIN(93,I)=YTRP6(NTRP6)*(XTRP6(NTRP6)/EN)**2*1.D-16*AMPTRP6       
  684 IF(EN.LE.(2.0*EIN(93))) GO TO 685 
