  685 CONTINUE
C LOAD BREMSSTRAHLUNG X-SECTIONS
C     QIN(94,I)=0.0
C     QIN(95,I)=0.0
C     QIN(96,I)=0.0
C     IF(EN.LE.1000.) GO TO 720
C     DO 700 J=2,NBREM
C     IF(EN.LE.EBRM(J)) GO TO 710
C 700 CONTINUE
C     J=NBREM
C 710 A=(DLOG(Z1T(J))-DLOG(Z1T(J-1)))/(EBRM(J)-EBRM(J-1))
C     B=(DLOG(Z1T(J))*EBRM(J-1)-DLOG(Z1T(J-1))*EBRM(J))/
C    /(EBRM(J-1)-EBRM(J))
C     A1=(DLOG(Z6T(J))-DLOG(Z6T(J-1)))/(EBRM(J)-EBRM(J-1))
C     B1=(DLOG(Z6T(J))*EBRM(J-1)-DLOG(Z6T(J-1))*EBRM(J))/
C    /(EBRM(J-1)-EBRM(J))
C     A2=(DLOG(Z8T(J))-DLOG(Z8T(J-1)))/(EBRM(J)-EBRM(J-1))
C     B2=(DLOG(Z8T(J))*EBRM(J-1)-DLOG(Z8T(J-1))*EBRM(J))/
C    /(EBRM(J-1)-EBRM(J))
C     QIN(94,I)=DEXP(A*EN+B)*6.0*1.D-24
C     QIN(95,I)=DEXP(A1*EN+B1)*2.0*1.D-24
C     QIN(96,I)=DEXP(A2*EN+B2)*1.D-24
C 720 CONTINUE
C---------------------------------------------------------------------
C CALCULATE XSEC SUMS
C ROTATIONAL SUMS
C     SUMR=0.0
C     SUMMTR=0.0
C     SUMSUPR=0.0
C     SUMSUPMTR=0.0
C     DO 800 K=1,26
C     SUMSUPMTR=SUMSUPMTR+XSECDUM(K)
