C 810 SUMVIB=SUMVIB+QIN(K,I)
C     WRITE(6,888) EN,QIN(53,I),QIN(54,I),QIN(55,I),QIN(56,I)
C 888 FORMAT(' EN=',D12.5,' SUP V8=',D12.4,' V8=',D12.4,' V6=',D12.4,
