c     SUMINEL=SUMR+SUMSUPR+SUMVIB+SUMATT+SUMION
C     WRITE(6,888) EN,SUMSUPR,SUMSUPMTR,SUMR,SUMMTR
C 888 FORMAT(' EN=',D12.5,' SUPROT=',D12.4,' SUPMTR=',D12.4,'SUMROT=',
C    /D12.4,' MTROT=',D12.4)
C     AINELMT=SUMSUPMTR+SUMMTR
C     QMTSUM=Q(2,I)+AINELMT
C     WRITE(6,889) QMTSUM,AINELMT,Q(2,I)
C 889 FORMAT(' QMTSUM=',D12.4,' AINELMT=',D12.4,' Q(2,I)=',D12.4)
C      WRITE(6,889) EN,SUMVIB,SUMATT,SUMION
C 889 FORMAT(' EN=',D12.5,' SUMVIB=',D12.4,' SUMATT=',D12.4,' SUMION=',
C    /D12.4)
