C     SUMTRIP=QIN(57,I)+QIN(63,I)+QIN(69,I)+QIN(75,I)+QIN(84,I)+
C    /QIN(93,I)
C     SUMSING=SUMEXC-SUMTRIP
C     WRITE(6,901) EN,SUMSING,SUMTRIP
C 901 FORMAT(' EN=',D12.5,' SUMSING=',D12.4,' SUMTRIP=',D12.4)
C  TOTAL        
C     Q(1,I)=Q(2,I)+SUMINEL
  900 CONTINUE       
C     IF(NANISO.EQ.0) THEN
C SET NON ROTATIONAL X-SECTIONS TO ISOTROPIC
C      DO 903 K=1,4000
C      DO 902 M=53,NIN
C      PEQIN(M,K)=0.0
C 902  CONTINUE
C 903  CONTINUE
