       GAMMA1=(EMASS2+2.0D0*EN)/EMASS2
       GAMMA2=GAMMA1*GAMMA1
       BETA=DSQRT(1.0D0-1.0D0/GAMMA2)
       BETA2=BETA*BETA
C CALC BORN BETHE VARIABLES
       X2=1.0D0/BETA2
       X1=X2*DLOG(BETA2/(1.0D0-BETA2))-1.0D0   
      ENDIF   
C CALC ELASTIC MT
      IF(EN.LE.XELM(2)) THEN
       QELA=YELT(1)*1.D-16
       QELM=YELM(1)*1.D-16
       EPS=0.0
       GO TO 52
      ENDIF
      DO 50 J=2,NELM                                                   
      IF(EN.LE.XELM(J)) GO TO 51                                       
   50 CONTINUE                                                          
      J=NELM
   51 YXJ=DLOG(YELM(J))
      YXJ1=DLOG(YELM(J-1))
      XNJ=DLOG(XELM(J))
      XNJ1=DLOG(XELM(J-1))
      A=(YXJ-YXJ1)/(XNJ-XNJ1)                         
      B=(XNJ1*YXJ-XNJ*YXJ1)/(XNJ1-XNJ)
      QELM=DEXP(A*DLOG(EN)+B)*1.D-16   
C  CALC ELASTIC  
      YXJ=DLOG(YELT(J))
      YXJ1=DLOG(YELT(J-1))
      A=(YXJ-YXJ1)/(XNJ-XNJ1)
      B=(XNJ1*YXJ-XNJ*YXJ1)/(XNJ1-XNJ)                         
      QELA=DEXP(A*DLOG(EN)+B)*1.D-16 
C ANGULAR DISTRIBUTION FACTOR ( OKRIMOVSKKY)
      A=(YEPS(J)-YEPS(J-1))/(XELM(J)-XELM(J-1))                         
      B=(XELM(J-1)*YEPS(J)-XELM(J)*YEPS(J-1))/(XELM(J-1)-XELM(J))      
      EPS=A*EN+B      
C EPSILON =1.0-YEPS
      EPS=1.0D0-EPS
   52 CONTINUE
      IF(NANISO.EQ.0) THEN
C ISOTROPIC SCATTERING
