       PEQEL(2,I)=0.5
      ELSE IF(NANISO.EQ.2) THEN
C ANISOTROPIC SCATTERING (OKHRIMOVSKKY)
       Q(2,I)=QELA
       PEQEL(2,I)=EPS
      ELSE IF(NANISO.EQ.1) THEN
C ANISTROPIC SCATTERING (BACKWARD FORWARD SCATTERING)
       Q(2,I)=QELA
