C SAVE COMPUTE TIME                                                     
      DO 1000 K=1,71                                                  
      J=72-K                                                            
      IF(EFINAL.LE.EIN(J)) NIN=J-1                                      
 1000 CONTINUE 
C                                                                       
      RETURN                                                            
      END   
