job.command:
  cd ${SUBMITTINGDIRECTORY}
  set run = `echo $INPUTFILE0 | awk -F\: '{print $1}'`
  set dir = `echo $INPUTFILE0 | awk -F\: '{print $2}'`
  set output  = LaserPlots.${run}.root
  set logFile  = ${run}B.log
  if ( -r $logFile || -r ${output}) exit 0;
#  root.exe -q -b -x 'LoopOverLaserTrees.C+("st_laser_*'$run'*.laser.root","'$output'")' >& ${logFile}
  root.exe -q -b -x 'bfc.C(-1,"lana,nodefault")' 'LanaTrees.C+("'${dir}'/*'${run}'*.laser.root","'${output}'")' >& ${logFile}
