job.command:
cd ${SUBMITTINGDIRECTORY}
setenv STARFPE NO
set input        = `echo $INPUTFILE0 | awk -F\: '{print $1}'`
set output       = D${input}.root
set log          = D${input}.log
if (-r ${output}) exit 0;
if (-r ${log}) exit 0;
ln -s ~/macros/.sl* .
root.exe -q -b lBichsel.C dEdxFit.C+  'kfQA.C+("'${input}'","'${output}'")'  >& ${log}
