job.command:
  cd ${SUBMITTINGDIRECTORY}
  set evtsFile = $INPUTFILE0;
  set file     = `basename $evtsFile` # .nt` #_4000evts_minb
#  set tfile    = `echo $file | sed -e 's/\./_/g'`
  set nfile    = $file
#  ln -s ${evtsFile} ${nfile}
  echo "${evtsFile}"
  if (! -r ${evtsFile}) exit 1;
  set logFile  = ${file}B.log
  if ( -r $logFile) exit 0;
  set fitter = "KFVertex,StiCA,HLTCA"
  set case = `basename $PWD`; echo "case = $case";
  echo $case | grep VMF
  if (! $?) set fitter = "VFMinuit,StiCA,HLTCA";
  echo "fitter $fitter"
  root4star -q -b 'bfc.C(100,"RC.y2011,dbV20111124,in,ReadAll,'${fitter}',NoHistos,NoRunco","'${evtsFile}'")' >&  ${logFile}
